from multiprocessing import Pool
import datetime

from tools import get_morph_predictor, get_word2vec_model, get_clean_rusvectores_words


def parallel_function(text):
    """ """
    morph_predictor = get_morph_predictor()
    word2vec_model = get_word2vec_model("models/180/model.bin")
    text_vectors = get_clean_rusvectores_words(text, morph_predictor, word2vec_model)

    return text_vectors




if __name__ == "__main__":
    print("------------------------------------------------------------------------------")
    print("Параллельная обработка")

    t = datetime.datetime.now()
